document.addEventListener("DOMContentLoaded",async function(){const n=[{id:"navbar-container",file:"/sections/navbar.html"},{id:"hero-container",file:"/sections/hero.html"},{id:"clients-container",file:"/sections/clients.html"},{id:"about-container",file:"/sections/about.html"},{id:"who-we-are-container",file:"/sections/who-we-are.html"},{id:"services-container",file:"/sections/services.html"},{id:"process-container",file:"/sections/process.html"},{id:"contact-container",file:"/sections/contact.html"},{id:"blog-container",file:"/sections/blog.html"},{id:"testimonials-container",file:"/sections/testimonials.html"},{id:"footer-container",file:"/sections/footer.html"}];for(const e of n){const t=document.getElementById(e.id);if(t)try{const i=await fetch(e.file);if(i.ok){const o=await i.text();t.innerHTML=o}else console.error(`Error loading ${e.file}: ${i.statusText}`)}catch(i){console.error(`Error fetching ${e.file}:`,i)}}window.initializeObservers&&setTimeout(()=>window.initializeObservers(),100),window.initializeNavbar&&window.initializeNavbar(),window.initializeSlider&&window.initializeSlider()});
